import json

def is_dengue_tweet(tweet):
    keywords = ['dengue']
    for k in keywords:
        if k in tweet['text'].lower():
            return True

    return False

def has_location_data(tweet):
    return tweet['place'] != None or tweet['coordinates'] != None

def check(data):
    json_tweet = json.loads(data)

    if is_dengue_tweet(json_tweet) and has_location_data(json_tweet):
        print('Check!')
    else:
        print('Not Check!')


data1 = '{"text" : "tengo Dengue", "place" : {"name" : "somewhere in central america"}, "coordinates": null}'
data2 = '{"text" : "tengo Dengue", "place" : null, "coordinates" : [1,2]}'
data3 = '{"text" : "null", "place" : {"name" : "somewhere in central america"}, "coordinates" : [1,2]}'

check(data1)
check(data2)
check(data3)

